
public class Greatest_Of_Two {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a =200;
		int b =20;
		
		if(a>b) {
			System.out.println(a + " is greater than b");
		}
		else if(a==b) {
			System.out.println("Equals");
		}
		else {
			System.out.println(b + " is greater than a");
		}
	}
}